import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const touristSpots = [
  {
    name: 'NITK Beach',
    image: 'data:image/jpeg;base64,',
    description: 'A tranquil haven by the Arabian Sea, is cherished for its serene ambiance and stunning coastal views, within walking distance from campus.'
  },
  {
    name: 'Panambur Beach',
    image: 'data:image/jpeg;base64,',
    description: 'A vibrant coastal getaway, known for its golden sands, thrilling water sports, and mesmerizing sunsets.'
  },
  {
    name: 'St Mary Island',
    image: 'data:image/jpeg;base64,',
    description: 'A paradise of unique hexagonal basalt rock formations, offers a serene escape amidst crystal-clear waters and pristine sands'
  },
  {
    name: 'Mulki Beach',
    image: 'data:image/jpeg;base64,',
    description: 'Comes alive at night with a mesmerizing display of bioluminescence, as tiny marine organisms light up the waves with their magical blue glow.'
  },
];



const Sightseeing = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  return (
    <section ref={ref} className="py-24 bg-gray-50" id="sightseeing">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: inView ? 1 : 0, y: inView ? 0 : 20 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl font-bold text-gray-800 mb-4">Tourist Attractions</h2>
          <p className="text-xl text-gray-600">Explore the beautiful places around NITK,Surathkal</p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {touristSpots.map((spot, index) => (
            <motion.div
              key={spot.name}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: inView ? 1 : 0, y: inView ? 0 : 20 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              className="bg-white rounded-lg shadow-lg overflow-hidden"
            >
              <img
                src={spot.image}
                alt={spot.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-6">
                <h3 className="text-xl font-semibold text-gray-800 mb-2">{spot.name}</h3>
                <p className="text-gray-600">{spot.description}</p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Sightseeing;
